import NextLink from 'next/link';
import { useRouter } from 'next/router';

interface ButtonProps extends React.HTMLProps<HTMLButtonElement> {
  href?: string;
}

export default function Button({ children, href, onClick }: ButtonProps) {
  const router = useRouter();
  const linkActive = router.asPath === href;

  if (href) {
    return (
      <NextLink href={href || '/'}>
        <a
          className={`underline decoration-2 underline-offset-2 ${
            linkActive
              ? 'font-bold decoration-red-500'
              : 'decoration-violet-500'
          }`}
        >
          {children}
        </a>
      </NextLink>
    );
  }

  return (
    <button
      className="rounded-md border-0 bg-violet-500 px-12 py-2 text-base text-white shadow-lg shadow-violet-300 transition hover:bg-violet-600 hover:shadow-violet-400"
      onClick={onClick}
    >
      {children}
    </button>
  );
}

